import { Component } from '@angular/core';
import { Todo } from './todo';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  todos: Todo[] = [];
  newTodo: string;

  saveTodo() {
    if (this.newTodo) {
      let todo = new Todo();
      todo.name = this.newTodo;
      todo.isCompleted = true
    } else {
      alert("Please entre Todo")
    }
  }
}
<html> 
  <head>
    <title></title> 
  </head>

  <body> 
    <h1 class="title"> Todo List </h1>
    <br>
    <div class="container text-center">
      <div class="row justify-content-md-center">
        <div class="col col-lg-3">
          <div class="input-group mb-3">
            <input type="text" class="form-control" placeholder="Enter Todo" aria-label="Enter Todo" aria-describedby="button-addon2">
            <button class="btn btn-success" type="button" id="button-addon2">Add Todo</button>
          </div>
          <table class="table table-striped">
            <tbody>
              <tr *ngFor="let todo of todos; let i = index">
                <th scope="row">{{i+1}}</th>
                  <td>{{todo.name}}</td>
                  <td class="delete-todo">X</td>
              </tr>
            </tbody>
          </table>
        </div>
      </div>
    </div>
  </body>
</html>
