import { Component } from '@angular/core';
import { Todo } from './todo';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  todos: Todo[] = [];
  newTodo: string;

  saveTodo() {
    if (this.newTodo) {
      let todo = new Todo();
      todo.name = this.newTodo;
      todo.isCompleted = true;
      this.todos.push(todo);
      this.newTodo = '';
    } else {
      alert("Please entre Todo")
    }
  }

  done(id: number) {
    this.todos[id].isCompleted = !this.todos[id].isCompleted;
  }

  remove(id: number) {
    this.todos = this.todos.filter((v,i) => i !== id)
  }
}
<html> 
  <head>
    <title></title> 
  </head>

  <body> 
    <h1 class="title"> Ma to do List </h1>
    <br>
    <div class="container text-center">
      <div class="row justify-content-md-center">
        <div class="col col-lg-3">
          <div class="input-group mb-3">
            <input type="text" class="form-control" placeholder="Enter Todo" aria-label="Enter Todo" aria-describedby="button-addon2" [(ngModel)]="newTodo">
            <button class="btn btn-success" type="button" id="button-addon2" (click)="saveTodo()">Ajouter un ToDo</button>
          </div>
          <table class="table table-striped">
            <tbody>
              <tr *ngFor="let todo of todos; let i = index">
                <th scope="row">{{i+1}}</th>
                  <td class="{{(!todo.isCompleted)?'done':''}}" (click)="done(i)">{{todo.name}}</td>
                  <td class="delete-todo" (click)="remove(i)"> Supprimer</td>
              </tr>
            </tbody>
          </table>
        </div>
      </div>
    </div>
  </body>
</html>
